-- 文章添加定时发布字段
ALTER TABLE `cms`.`cms_archive` ADD COLUMN `schedule_time` INT(10) NOT NULL DEFAULT 0 COMMENT '定时发布时间' AFTER `thumbnail`;ALTER TABLE `cms`.`cms_archive` CHANGE COLUMN `id` `id` INT(10) NOT NULL AUTO_INCREMENT FIRST, CHANGE COLUMN `str_id` `str_id` VARCHAR(16) NOT NULL COLLATE utf8mb3_general_ci AFTER `id`, CHANGE COLUMN `site_id` `site_id` INT(10) NOT NULL COMMENT '站点编号' AFTER `str_id`, CHANGE COLUMN `alias` `alias` VARCHAR(50) NULL DEFAULT NULL COMMENT ' 别名' COLLATE utf8mb3_general_ci AFTER `site_id`, CHANGE COLUMN `cat_id` `cat_id` INT(10) NOT NULL COMMENT '栏目编号' AFTER `alias`, CHANGE COLUMN `path` `path` VARCHAR(180) NOT NULL COMMENT '文档路径' COLLATE utf8mb3_general_ci AFTER `cat_id`, CHANGE COLUMN `flag` `flag` INT(10) NOT NULL COMMENT '文档标志' AFTER `path`, CHANGE COLUMN `author_id` `author_id` INT(10) NOT NULL COMMENT '作者编号' AFTER `flag`, CHANGE COLUMN `title` `title` VARCHAR(100) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `author_id`, CHANGE COLUMN `small_title` `small_title` VARCHAR(100) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `title`, CHANGE COLUMN `location` `location` VARCHAR(150) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `small_title`, CHANGE COLUMN `sort_number` `sort_number` INT(10) NULL DEFAULT '0' AFTER `location`, CHANGE COLUMN `source` `source` VARCHAR(50) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `sort_number`, CHANGE COLUMN `tags` `tags` VARCHAR(100) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `source`, CHANGE COLUMN `outline` `outline` VARCHAR(255) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `tags`, CHANGE COLUMN `content` `content` TEXT NULL COLLATE utf8mb3_general_ci AFTER `outline`, CHANGE COLUMN `view_count` `view_count` INT(10) NULL DEFAULT '0' AFTER `content`, CHANGE COLUMN `agree` `agree` INT(10) NULL DEFAULT NULL AFTER `view_count`, CHANGE COLUMN `disagree` `disagree` INT(10) NULL DEFAULT NULL AFTER `agree`, CHANGE COLUMN `thumbnail` `thumbnail` VARCHAR(150) NULL DEFAULT NULL COLLATE utf8mb3_general_ci AFTER `disagree`, CHANGE COLUMN `create_time` `create_time` INT(10) NOT NULL COMMENT '创建时间' AFTER `schedule_time`, CHANGE COLUMN `update_time` `update_time` INT(10) NOT NULL COMMENT '修改时间' AFTER `create_time`