        /// <summary>
        /// 合并代码
        /// </summary>
        public void Combine()
        {

            CacheUtil.Output(Response, int.MaxValue, () =>
            {
                var path = Request.Query("loc").ToString() ?? "";
                var type = Request.Query("res_combine").ToString() ?? "js";

                var files = path.Split(',');
                var appDir = Cms.PhysicPath;

                const bool compress = true;

                foreach (var file in files)
                    if (File.Exists(appDir + file))
                    {
                        if (compress && (type == "js" || type == "css"))
                        {
                           Response.WriteAsync(CompressHtml(File.ReadAllText(appDir + file)));
                        }
                        else
                        {
                            var bytes = File.ReadAllBytes(appDir + file);
                            Response.Write(bytes, 0, bytes.Length);
                        }
                    }

                switch (type.ToLower())
                {
                    case "js":
                        Response.ContentType ( "text/javascript");
                        break;
                    case "css":
                        Response.ContentType (  "text/css");
                        break;
                    case "img":
                        Response.ContentType (  "image/jpeg");
                        break;
                    default:
                          Response.ContentType ( "application/oct-stream");
                        break;
                }

                return "";
            });
        }
